[C#]

	ARSystem.FieldValueList fieldValueList = new ARSystem.FieldValueList();
	// Character data type
	fieldValueList[8] = "Test Insert";
	// Character
	fieldValueList[2] = "Test";
	// Diary data type
	fieldValueList[536870913] = "New diary entry";
	// Attachment data type - Multiple ways to set
	// Case.1:
	System.IO.Stream fileStream = System.IO.File.Open("..\\..\\TEST.JPG", System.IO.FileMode.Open, System.IO.FileAccess.Read);
	byte [] bytes = new byte[fileStream.Length];
	fileStream.Read(bytes, 0, (int)fileStream.Length);
	fileStream.Close();
	ARSystem.Attachment attach = new ARSystem.Attachment();
	attach.SetValue("Test.JPG", bytes);
	fieldValueList[536880912] = attach;
	// Case.2:
	System.IO.Stream stream = new FileStream(@"c:\temp\foo.txt", FileMode.Open); 
              BMC.ARSystem.Attachment att = new BMC.ARSystem.Attachment();
              att.SetValue("SomeAttachment", stream); 
              fieldValueList[536880912] = att;
              // Case.3:
              fieldValueList[536880912] = new Attachment("My attachment label", @"c:\temp\foo.txt");
              // Case.4:
              fieldValueList[536880912] = new Attachment(@"c:\temp\foo.txt");
	// Currency datatype - Multiple ways to set
	// Case.1:
	ARSystem.CurrencyValue curValue = new ARSystem.CurrencyValue();
	curValue.CurrencyCode = "USD";
	curValue.Value = 103.5m;
	fieldValueList[536870914] = curValue;
	// Case.2:
	fieldValueList[536870914] = new CurrencyValue(3222, "USD");
	// Case.3:
	fieldValueList[536870914] = "320.45 USD";
	// Date/Time datatype
	fieldValueList[536870916] = new System.DateTime(2004, 02, 02).ToUniversalTime();
	// Decimal datatype
	fieldValueList[536870917] = 135.75m; 
	// Date datatype - Multiple ways to set
	// Case.1:
	fieldValueList[536870918] = server.DateToJulianDate(2002, 10, 20); // Date value = Oct 20, 2002
	// Case.2:
	fieldValueList[536870918] = 125000; // Julian date encoded into integer representation
	// Time of day datatype
	fieldValueList[536870919] = (((6 * 60)  + 32) * 60) + 27; // Time of day = 6:32:27AM
	server.CreateEntry(formName, fieldValueList);

