    int[] request = new int [] {
        Constants.AR_SERVER_INFO_UNQUAL_QUERIES,
        Constants.AR_SERVER_INFO_FILTER_LOG_FILE
        };
    IDictionary oldInfo = server.GetServerInfo(request);
    Console.WriteLine("  Allow unqualified searches (0/1): " + oldInfo[Constants.AR_SERVER_INFO_UNQUAL_QUERIES]);
    Console.WriteLine("  Filter log file path is: " + oldInfo[Constants.AR_SERVER_INFO_FILTER_LOG_FILE]);
    Console.WriteLine("Change the above two server settings...");
	IDictionary newInfo = new Hashtable();
    newInfo[Constants.AR_SERVER_INFO_UNQUAL_QUERIES] = 0;
    newInfo[Constants.AR_SERVER_INFO_FILTER_LOG_FILE] = "myfilterlog.log";
    server.SetServerInfo(newInfo);
    Console.WriteLine("Retrieve the modified settings and print...");
    IDictionary changedInfo = server.GetServerInfo(request);
    Console.WriteLine("  Allow unqualified searches (0/1): " + changedInfo[Constants.AR_SERVER_INFO_UNQUAL_QUERIES]);
    Console.WriteLine("  Filter log file path is: " + changedInfo[Constants.AR_SERVER_INFO_FILTER_LOG_FILE]);
    Console.WriteLine("Restore original settings...");
    server.SetServerInfo(oldInfo);
    Console.WriteLine("Verify that the settings are restored...");
    oldInfo = server.GetServerInfo(request);
    Console.WriteLine("  Allow unqualified searches (0/1): " + oldInfo[Constants.AR_SERVER_INFO_UNQUAL_QUERIES]);
    Console.WriteLine("  Filter log file path is: " + oldInfo[Constants.AR_SERVER_INFO_FILTER_LOG_FILE]);

